import './assets/css/App.css';
import React, {useState, useEffect, useRef} from 'react';
import List from './components/List.jsx'
import Form from './components/Form.jsx'

const generateUniqueId = require('generate-unique-id');

function App() {

  const todoList = [
    {id: generateUniqueId({useLetters: false}), task: 'Cook', priority: "Medium", done: false},
    {id: generateUniqueId({useLetters: false}), task: 'Call dad', priority: "Low", done: false},
    {id: generateUniqueId({useLetters: false}), task: 'Laundry', priority: "High", done: false}
  ]

  // State (datos)
  const [todos, setTodos] = useState(todoList)

  // Agregar todo
  const addTodo = (newTodo) => {
    newTodo.id = generateUniqueId({useLetters: false});
    newTodo.done = false;
    setTodos([
      ...todos,
      newTodo
    ])
  }

  // Cambiar estado del todo
  const switchDone = (id) => {
    setTodos(todos.map(todo => todo.id === id ? {...todo, done: !todo.done} : {...todo})
    )
  }

  // Eliminar todo de la lista
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const prevCount = usePrevious(todos)

  function usePrevious(todos) {
    const ref = useRef();

    useEffect(() => {
      ref.current = todos;
    }, [todos])

    return ref.current
  }



