  return (
    <div className="App">
      <header className="App-header">
        TODO LIST
      </header>
      <section className="App-body">
      <Form addTodo={addTodo}/>
      <List todos={todos} switchDone={switchDone} deleteTodo={deleteTodo}/>
      </section>
    </div>
  );
}

export default App;
