import './assets/css/App.css';
import React, {useState} from 'react';
import List from './components/List.jsx'
import Form from './components/Form.jsx'

const generateUniqueId = require('generate-unique-id');

function App() {

  const todoList = [
    {id: generateUniqueId({useLetters: false}), task: 'Cook', priority: "Medium", done: false},
    {id: generateUniqueId({useLetters: false}), task: 'Call dad', priority: "Low", done: false},
    {id: generateUniqueId({useLetters: false}), task: 'Laundry', priority: "High", done: false}
  ]

  // State (datos)
  const [todos, setTodos] = useState(todoList)

  // Agregar todo
  const addTodo = (newTodo) => {
    newTodo.id = generateUniqueId({useLetters: false});
    newTodo.done = false;
    setTodos([
      ...todos,
      newTodo
    ])
  }

  // Cambiar estado del todo
  const switchDone = (id) => {
    setTodos(todos
      .map(todo => todo.id === id ? {...todo, done: !todo.done} : {...todo})
      .sort((a, b) => a.done === false && b.done === true ? -1 : a.priority === 'Medium' && b.priority === 'High' ? -1 : 1)
    )
  }

  // Eliminar todo de la lista
  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  //Ordenar todos

    /* setTodos(todos.sort(function(a, b) {
      if (a.done === true && b.done === false) {return -1}
      if (a.done === false && b.done === false) {
        if (a.priority === 'Medium' && b.priority === 'High') {return -1}
        if (a.priority === 'Low' && b.priority === 'High') {return -1}
        if (a.priority === 'Low' && b.priority === 'Medium') {return -1}
      }
      else {return 0}
    }))  */

  return (
    <div className="App">
      <header className="App-header">
        TODO LIST
      </header>
      <section className="App-body">
      <Form addTodo={addTodo}/>
      <List todos={todos} switchDone={switchDone} deleteTodo={deleteTodo}/>
      </section>
    </div>
  );
}

export default App;
